mapper.select:
	select *
 	  from (
			select rownum rnum, a.*
			  from (select *
					  from tbl_board
					 order by no desc
					) a
			)
	 where rnum between #{begin} and #{end}

mapper.insert:

    insert into tbl_board(           
	     title, content, id, genre   
	) values (                       
	    #{title}, #{content}, #{id}, 1 
	)
