mapper.insert:
    insert into tbl_board(           
	    title, content, id, genre   
	) values (                       
	   #{title}, #{content}, #{id}, 1 
	)

    insert into tbl_file(           
	   path, oriName, sysName, size, bno
	) values (                       
	     #{path}, #{oriName}, #{sysName}, #{size} , #{bno}
	)

mapper.update:
update tbl_file
   set path = #{path},
   	   size = #{size},
   	   oriName = #{oriName},
   	   sysName = #{sysName}
 where bno=#{bno}

mapper.select:
select count(*)
  from tbl_like
 where bno = #{bno}

select *
  from tbl_like
 where id = #{id}
   and bno = #{bno}

mapper.delete:
delete
  from tbl_like
 where bno = #{bno}
   and id = #{id}

mapper.insert:
insert into tbl_like (
	bno,
	id
) values (
	#{bno},
	#{id}
)
